mapper.update:

update t_article
set votes = votes + #{arg1,jdbcType=INTEGER}
where id = #{arg0,jdbcType=INTEGER}


update t_article
set status = #{arg1,jdbcType=INTEGER}
where id = #{arg0,jdbcType=INTEGER}

mapper.select:

    select id, title, content, votes, author, crowd, scene, status, create_date, update_date
    from t_article


			author = #{author,jdbcType=VARCHAR}


			AND status = #{status,jdbcType=VARCHAR}
